pragma solidity ^0.5.16;

contract StockStorage {
    string private _name = "inStoreInformation"; 
    uint8 private _decimals = 8;

    struct StoreInfo {
      bytes32 name;
      uint32 amount;
    }

    struct StockInfo {
        bytes32 addr;
        bytes32 telno;
    }

    mapping (address => StoreInfo) private _stores;
    mapping (address => StockInfo) private _stocks;
    // allownce access
    mapping(address => bool) private accessAllowed;

    constructor()public{
        accessAllowed[msg.sender] = true;
    }

    modifier isPlatform() {
        require(accessAllowed[msg.sender] == true,"the address not allowed the storage!");
        _;
    }

    function allowAccess(address _address) isPlatform public {
        accessAllowed[_address] = true;
    }

    function denyAccess(address _address) isPlatform public {
        accessAllowed[_address] = false;
    }


    function stringToBytes32(string memory source)private pure returns (bytes32 result) {
        assembly {
            result := mload(add(source, 32))
        }
    }

    function getName() public view returns (bytes32) {
        return stringToBytes32(_name);
    }

    function getDecimals() public view returns (uint8) {
        return _decimals;
    }

    function getStock(address _address) public view returns (bytes32,bytes32) {
        return (_stocks[_address].addr,_stocks[_address].telno);
    }

    function setStock(address _addr,bytes32 _stockaddr,bytes32 _telno) public returns (bool){
        StockInfo memory stockInfo;
        stockInfo.addr = _stockaddr;
        stockInfo.telno = _telno;
        _stocks[_addr] = stockInfo;
        return true;
    }

    function getStore(address _address) public view returns (bytes32,uint32) {
        return (_stores[_address].name,_stores[_address].amount);
    }

    function setStore(address _addr,bytes32 _storename,uint32 _amount) public returns (bool){
        StoreInfo memory storeInfo;
        storeInfo.name = _storename;
        storeInfo.amount = _amount;
        _stores[_addr] = storeInfo;
        return true;
    }
}
