pragma solidity ^0.5.16;

contract TokenStorage {
    bool private inited = false;
    string private _symbol = "LDP";
    string private _name = "ldpToken";
    uint8 private _decimals = 8;
    uint64 private _totalSupply = 1000000;
    mapping (address => uint) private _balanceOf;
    mapping (address => mapping (address => uint)) private _allowances;
    // allownce access
    mapping(address => bool) private accessAllowed;

    constructor()public{
        accessAllowed[msg.sender] = true;
        _balanceOf[tx.origin] = _totalSupply;
    }

    modifier isPlatform() {
        require(accessAllowed[msg.sender] == true,"the address not allowed the storage!");
        _;
    }

    function allowAccess(address _address) isPlatform public {
        accessAllowed[_address] = true;
    }

    function denyAccess(address _address) isPlatform public {
        accessAllowed[_address] = false;
    }

    function getTotalSupply() public view returns (uint) {
        return _totalSupply;
    }

    function getBalanceOf(address _addr) public view returns (uint){
        return _balanceOf[_addr];
    }
    // set Transfer value
    function setBalanceOf(address _addr ,  uint _value) isPlatform public {
        _balanceOf[_addr] =  _value;
    }
}
